// src/components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Users, 
  Shield, 
  Trash2, 
  Edit2, 
  X, 
  Key, 
  RefreshCw,
  BarChart3,
  Mail,
  User,
  Palette,
  Save,
  AlertTriangle
} from 'lucide-react';

function AdminPanel({ apiUrl, user, onInviteCodeUpdate }) {
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    color: '',
    role: 'member'
  });
  const [newPassword, setNewPassword] = useState('');

  const COLORS = [
    '#ef4444', '#f59e0b', '#10b981', '#3b82f6', 
    '#8b5cf6', '#ec4899', '#14b8a6', '#f97316'
  ];

  useEffect(() => {
    if (user?.role === 'admin') {
      fetchUsers();
      fetchStats();
    }
  }, [user]);

  const fetchUsers = async () => {
    try {
      const response = await axios.get(`${apiUrl}/admin/users`);
      setUsers(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching users:', error);
      alert('BÅ‚Ä…d podczas pobierania uÅ¼ytkownikÃ³w');
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get(`${apiUrl}/admin/stats`);
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleEditUser = (userToEdit) => {
    setEditingUser(userToEdit);
    setFormData({
      name: userToEdit.name,
      email: userToEdit.email,
      color: userToEdit.color,
      role: userToEdit.role
    });
    setShowEditModal(true);
  };

  const handleSaveUser = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`${apiUrl}/admin/users/${editingUser.id}`, formData);
      setShowEditModal(false);
      fetchUsers();
      alert('UÅ¼ytkownik zaktualizowany pomyÅ›lnie');
    } catch (error) {
      console.error('Error updating user:', error);
      alert(error.response?.data?.error || 'BÅ‚Ä…d podczas aktualizacji uÅ¼ytkownika');
    }
  };

  const handleChangePassword = (userToEdit) => {
    setEditingUser(userToEdit);
    setNewPassword('');
    setShowPasswordModal(true);
  };

  const handleSavePassword = async (e) => {
    e.preventDefault();

    if (newPassword.length < 6) {
      alert('HasÅ‚o musi mieÄ‡ minimum 6 znakÃ³w');
      return;
    }

    try {
      await axios.put(`${apiUrl}/admin/users/${editingUser.id}/password`, {
        newPassword
      });
      setShowPasswordModal(false);
      setNewPassword('');
      alert('HasÅ‚o zostaÅ‚o zmienione pomyÅ›lnie');
    } catch (error) {
      console.error('Error changing password:', error);
      alert(error.response?.data?.error || 'BÅ‚Ä…d podczas zmiany hasÅ‚a');
    }
  };

  const handleDeleteUser = async (userToDelete) => {
    if (userToDelete.id === user.id) {
      alert('Nie moÅ¼esz usunÄ…Ä‡ samego siebie');
      return;
    }

    if (!window.confirm(`Czy na pewno chcesz usunÄ…Ä‡ uÅ¼ytkownika ${userToDelete.name}? Ta operacja jest nieodwracalna!`)) {
      return;
    }

    try {
      await axios.delete(`${apiUrl}/admin/users/${userToDelete.id}`);
      fetchUsers();
      alert('UÅ¼ytkownik zostaÅ‚ usuniÄ™ty');
    } catch (error) {
      console.error('Error deleting user:', error);
      alert(error.response?.data?.error || 'BÅ‚Ä…d podczas usuwania uÅ¼ytkownika');
    }
  };

  const handleRegenerateInviteCode = async () => {
    if (!window.confirm('Czy na pewno chcesz wygenerowaÄ‡ nowy kod zaproszenia? Stary kod przestanie dziaÅ‚aÄ‡!')) {
      return;
    }

    try {
      const response = await axios.post(`${apiUrl}/admin/regenerate-invite`);
      alert(`Nowy kod zaproszenia: ${response.data.inviteCode}`);
      if (onInviteCodeUpdate) {
        onInviteCodeUpdate(response.data.inviteCode);
      }
    } catch (error) {
      console.error('Error regenerating invite code:', error);
      alert('BÅ‚Ä…d podczas generowania kodu');
    }
  };

  if (user?.role !== 'admin') {
    return (
      <div className="admin-panel">
        <div className="access-denied">
          <AlertTriangle size={64} />
          <h2>Brak dostÄ™pu</h2>
          <p>Tylko administratorzy majÄ… dostÄ™p do tego panelu</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="admin-panel">
        <div className="loading">
          <div className="loading-spinner"></div>
          <p>Åadowanie...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <div className="admin-title">
          <Shield size={32} />
          <div>
            <h2>Panel Administracyjny</h2>
            <p>ZarzÄ…dzanie rodzinÄ… i uÅ¼ytkownikami</p>
          </div>
        </div>
      </div>

      {/* Statistics */}
      {stats && (
        <div className="admin-stats">
          <h3><BarChart3 size={20} /> Statystyki</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-icon">ğŸ‘¥</div>
              <div className="stat-value">{stats.usersCount}</div>
              <div className="stat-label">UÅ¼ytkownikÃ³w</div>
            </div>
            <div className="stat-card">
              <div className="stat-icon">ğŸ“…</div>
              <div className="stat-value">{stats.eventsCount}</div>
              <div className="stat-label">WydarzeÅ„</div>
            </div>
            <div className="stat-card">
              <div className="stat-icon">ğŸ›’</div>
              <div className="stat-value">{stats.shoppingListsCount}</div>
              <div className="stat-label">List zakupÃ³w</div>
            </div>
            <div className="stat-card">
              <div className="stat-icon">âœ…</div>
              <div className="stat-value">{stats.todosCount}</div>
              <div className="stat-label">ZadaÅ„</div>
            </div>
            <div className="stat-card">
              <div className="stat-icon">ğŸ“</div>
              <div className="stat-value">{stats.notesCount}</div>
              <div className="stat-label">Notatek</div>
            </div>
          </div>
        </div>
      )}

      {/* Invite Code */}
      <div className="admin-section">
        <h3>Kod Zaproszenia</h3>
        <button onClick={handleRegenerateInviteCode} className="btn-regenerate">
          <RefreshCw size={18} />
          Wygeneruj nowy kod
        </button>
        <p className="invite-note">
          Uwaga: Wygenerowanie nowego kodu uniewaÅ¼ni stary kod zaproszenia
        </p>
      </div>

      {/* Users Management */}
      <div className="admin-section">
        <h3><Users size={20} /> ZarzÄ…dzanie UÅ¼ytkownikami</h3>

        <div className="users-list">
          {users.map(u => (
            <div key={u.id} className="user-card">
              <div className="user-avatar" style={{ backgroundColor: u.color }}>
                {u.name.charAt(0).toUpperCase()}
              </div>

              <div className="user-info">
                <div className="user-name">
                  {u.name}
                  {u.role === 'admin' && (
                    <span className="admin-badge">
                      <Shield size={14} />
                      Admin
                    </span>
                  )}
                  {u.id === user.id && (
                    <span className="you-badge">Ty</span>
                  )}
                </div>
                <div className="user-email">{u.email}</div>
                <div className="user-meta">
                  DoÅ‚Ä…czyÅ‚: {new Date(u.created_at).toLocaleDateString('pl-PL')}
                </div>
              </div>

              <div className="user-actions">
                <button
                  onClick={() => handleEditUser(u)}
                  className="btn-icon"
                  title="Edytuj uÅ¼ytkownika"
                >
                  <Edit2 size={18} />
                </button>
                <button
                  onClick={() => handleChangePassword(u)}
                  className="btn-icon"
                  title="ZmieÅ„ hasÅ‚o"
                >
                  <Key size={18} />
                </button>
                {u.id !== user.id && (
                  <button
                    onClick={() => handleDeleteUser(u)}
                    className="btn-icon btn-danger"
                    title="UsuÅ„ uÅ¼ytkownika"
                  >
                    <Trash2 size={18} />
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Edit User Modal */}
      {showEditModal && (
        <div className="modal-overlay" onClick={() => setShowEditModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Edytuj uÅ¼ytkownika</h3>
              <button onClick={() => setShowEditModal(false)}>
                <X size={24} />
              </button>
            </div>

            <form onSubmit={handleSaveUser} className="modal-form">
              <div className="form-group">
                <label>
                  <User size={18} />
                  ImiÄ™
                </label>
                <input
                  type="text"
